css:
/* General Styling */
.container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.card {
  width: 350px;
  border-radius: 12px;
  padding: 20px;
  font-family: 'Arial', sans-serif;
  border: 2px solid rgba(255, 255, 255, 0.2);
}

/* Dynamic Background & Font Colors */
.container.amateur .card {
  background-color: #34495E; /* Steel Blue */
  color: #ECF0F1; /* Soft White */
}

.container.professional .card {
  background-color: #2C3E50; /* Deep Navy */
  color: #F1C40F; /* Gold */
  border: 2px solid #F1C40F;
}

.container.junior .card {
  background-color: #1B4F72; /* Deep Blue */
  color: #AED6F1; /* Sky Blue */
}

.container.elite .card {
  background: linear-gradient(135deg, #0F0F0F, #1C1C1C); /* Jet Black */
  color: #FFD700; /* Gold */
  border: 3px solid #FFD700;
}

/* Header & Profile Image */
.card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid rgba(255, 255, 255, 0.3);
  padding-bottom: 10px;
  margin-bottom: 10px;
}

.profile-photo {
  width: 75px;
  height: 75px;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid white;
}

/* License ID */
.license-id {
  text-align: center;
  font-size: 18px;
  font-weight: bold;
  margin-bottom: 12px;
  border-top: 1px solid rgba(255, 255, 255, 0.3);
  padding-top: 10px;
}

.license-id .value {
    color: white;
}
/* Information Rows */
.info-row {
  display: flex;
  justify-content: space-between;
  padding: 7px;
  border-radius: 6px;
  margin-bottom: 7px;
  font-size: 20px;
}

.label {
  font-weight: bold;
}

.container.elite .card .card-body .info-row .value {
  color: #FFD700; /* Gold */
  font-size: 15px;
  margin: 0px;
}

.container.professional .card .card-body .info-row .value {
  color: #F1C40F; /* Gold */
  font-size: 15px;
  margin: 0px;
}
.container.amateur .card .card-body .info-row .value {
  color: #ECF0F1;
  font-size: 15px;
  margin: 0px;
}
.container.junior .card .card-body .info-row .value {
  color: #AED6F1; /* Sky Blue */
  font-size: 15px;
  margin: 0px;
}


/* Signature */
.signature {
  max-width: 160px;
  max-height: 35px;
  object-fit: contain;
}

/* Print Optimization */
@media print {
  body {
    background: none;
  }

  .card {
    box-shadow: none;
    border: 1px solid black;
  }
}
